alicloud-datasource-account.body:
data "alicloud_account" "$1" {
    # Exported Attributes
    # "id" - Account ID (e.g. "1239306421830812"). It can be used to construct an ARN.
}

alicloud-datasource-ram-account-alias.body:
data "alicloud_ram_account_aliases" "$1" {
    # output_file - (Optional) File name where to save data source results (after running `terraform plan`).
    output_file = ""

    # Exported Attributes
    # "account_alias" - Alias of the account.
}

alicloud-resource-ram-account-alias.body:
resource "alicloud_ram_account_alias" "$1" {
    # account_alias - (Optional) (Required, Forces new resource) Alias of cloud account. This name can have a string of 3 to 32 characters, must contain only alphanumeric characters or hyphens, such as "-", and must not begin with a hyphen.
    account_alias = ""

    # Exported Attributes
    # "account_alias" - The account alias.
}
